mapper.select:
SELECT * FROM ( SELECT INNER_TABLE.*, ROWNUM AS row_seq FROM (SELECT * FROM (SELECT U.USER_ID , U.EMAIL, UT.TAG_NAME FROM USERS U, USER_TAG UT WHERE U.USER_ID = UT.USER_ID) SELECT_TABLE
TAG_NAME LIKE '${searchKeyword}%'
OR EMAIL LIKE '${searchKeyword}%'
) INNER_TABLE WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
   	UPDATE USERS
   	SET
   		EMAIL = #{email},
		PWD	= #{pwd}
   	WHERE  USER_ID= #{userId}
